import React, { useState } from "react";

const Counter = () => {
  const [number, setNumber] = useState(0);
  const clichHandler = () => {
    setNumber(number + 1);
  };

  return (
    <div>
      <h1>{number}</h1>
      {/*Use the setNumber directly but notice that u should use Arrow Function*/}
      {/* <button onClick={() => setNumber(number + 1)}>Up One</button> */}
      <button onClick={clichHandler}>Up One</button>
    </div>
  );
};

export default Counter;
