import React,{useReducer} from 'react';

const initialState={
    num1:0,
};
const reducer=(state,action)=>{
    switch (action.type) {
        case "Up":
            return {num1: state.num1+action.amount}         
        case "Down":
            return {num1: state.num1-action.amount}
        case "Reset" :
            return initialState
    
        default:
            return state
    }
}
const Counter = () => {
    const [number, dispatch] = useReducer(reducer, initialState);
    return (
        <div>
            
            <h1>{number.num1}</h1>
            <button onClick={()=>dispatch({type: "Up" , amount:5})}>Up</button><br/><br/>
            <button onClick={()=>dispatch({type: "Reset"})}>Reset</button><br/><br/>
            <button onClick={()=>dispatch({type: "Down", amount:3})}>Down</button><br/><br/>
            
        </div>
    );
};

export default Counter;
