import React, { Component } from 'react';

class ClassEvent extends Component{

  constructor() {
    super();
    this.state = {
      paragraph: 'text 1',
    };
  }

  clickHandler = () => {
    this.setState({
      paragraph: 'text 3',
    });
  };

  render() {
    return (
      <div>
        <button onClick={this.clickHandler}>Class Event</button>
        <br />
        <br />
        <p>{this.state.paragraph}</p>
      </div>
    );
  }
}

export default ClassEvent;
