class ClassEvent extends Component {
  constructor() {
    super();
    this.state = {
      text: "Marzieh",
    };
  }

  changeHandler = (event) => {
    this.setState({
      text: event.target.value,
    });
  };

  render() {
    return (
      <div>
        <input
          type="text"
          value={this.state.text}
          onChange={this.changeHandler}
        />
        <select>
          <option value="option 1">option 1</option>
          <option value="option 1">option 1</option>
          <option value="option 1">option 1</option>
          <option value="option 1">option 1</option>
        </select>
      </div>
    );
  }
}

export default ClassEvent;
