import React, { Component } from "react";
import axios from "axios";

class SendPost extends Component {
  constructor(props) {
    super(props);
    this.state = {
      title: "",
    };
  }

  changeHandler = (event) => {
    this.setState({
      title: event.target.value,
    });
  };

  postHandler = () => {
    const text = this.state.title;
    axios.post("https://jsonplaceholder.typicode.com/posts", {title: text})
    .then(response=> console.log(response));
    //we can definition axios object in another way
    const data={}
  };

  render() {
    const { title } = this.state;
    return (
      <div>
        <input onChange={this.changeHandler} type="text" value={title} />
        <button onClick={this.postHandler}>Send Post</button>
      </div>
    );
  }
}

export default SendPost;
