import React, { useEffect, useState } from "react";
import { validate } from "./validate.js";
import { ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { notify } from "./toastify.js";
const SignUp = () => {
  //State1 : The main object
  const [data, setData] = useState({
    name: "",
    email: "",
    password: "",
    confirmPassword: "",
    isAccepted: false,
  });

  //State2 : The validation Error
  const [errors, setErrors] = useState({});

  //State3 : The touched situation
  const [touched, setTouched] = useState({});

  //UseEffect for change the object and show he error
  useEffect(() => {
    setErrors(validate(data));
  }, [data,touched]);

  //ChangeHandler//
  const changeHandler = (event) => {
    if (event.target.name === "isAccepted") {
      setData({ ...data, [event.target.name]: event.target.checked });
    } else {
      setData({ ...data, [event.target.name]: event.target.value });
    }
  };

  //FocusHandler
  const focusHandler = (event) => {
    setTouched({ ...touched, [event.target.name]: true });
  };

  //SubmitHandler
  const submitHandler=(event)=>{
    event.preventDefault();
    if(!Object.key(errors).length){
      notify("ðŸ˜ŠðŸ‘Œ  You signed successfully", "success");
      console.log(data);
    } else {
      notify("ðŸ˜¢ Invalid data!", "error");
      setTouched({
        name: true,
        email: true,
        password: true,
        confirmPassword: true,
        isAccepted: true,
      });
    }


  }

  //Return//
  return (
    <div>
      <form onSubmit={submitHandler}>
        <h1>SignUp</h1>
        <div>
          <label>Name</label>
          <input
            type="text"
            name="name"
            value={data.name}
            onChange={changeHandler}
            onFocus={focusHandler}
          />
          {errors.name && touched.name && <span>{errors.name}</span>}
        </div>
        <div>
          <label>Email</label>
          <input
            type="text"
            name="email"
            value={data.email}
            onChange={changeHandler}
            onFocus={focusHandler}
          />
          {errors.email && touched.email && <span>{errors.email}</span>}
        </div>
        <div>
          <label>Password</label>
          <input
            type="password"
            name="password"
            value={data.password}
            onChange={changeHandler}
            onFocus={focusHandler}
          />
          {errors.password && touched.password && <span>{errors.password}</span>}
        </div>
        <div>
          <label>Confirm Password</label>
          <input
            type="password"
            name="confirmPassword"
            value={data.confirmPassword}
            onChange={changeHandler}
            onFocus={focusHandler}
          />
          {errors.confirmPassword && touched.confirmPassword && <span>{errors.confirmPassword}</span>}
        </div>
        <div>
          <label>I accept terms of privacy policy</label>
          <input
            type="checkbox"
            name="isAccepted"
            value={data.isAccepted}
            onChange={changeHandler}
            onFocus={focusHandler}
          />
          {errors.isAccepted && touched.isAccepted && <span>{errors.isAccepted}</span>}
        </div>
        <div>
          <a href="www.google.com">Login</a>
          <button type="submit">SignUp</button>
        </div>
      </form>
      <ToastContainer/>
    </div>
  );
};

export default SignUp;
