class App extends Component {
  constructor(props) {
    super();
    this.state = {
      text: "Hi",
    };
  }
  resetHandler = () => {
    this.setState = {
      text: "Hi",
    };
  };

  render() {
    return (
      <div>
        <button onClick={this.resetHandler}>Reset</button>
        <h1>{this.state.text}</h1>
      </div>
    );
  }
}

export default App;
