import React, { Component } from "react";
import axios from "axios";

class SendPost extends Component {
  constructor(props) {
    super(props);
    this.state = {
      title: "",
      body: "",
    };
  }

  changeHandler = (event) => {
    this.setState({
      title: event.target.value,
    });
  };

  bodyChangeHandler = (event) => {
    this.setState({
      body: event.target.value,
    });
  };

  postHandler = () => {
    const data = {
      title: this.state.title,
    };
    axios
      .post("https://jsonplaceholder.typicode.com/posts", { data })
      .then((response) => console.log(response));
  };

  render() {
    const { title, body } = this.state;
    return (
      <div>
        <label>Title : </label>
        <input onChange={this.changeHandler} type="text" value={title} />
        <label>Body : </label>
        <input onChange={this.bodyChangeHandler} type="text" value={body} />
        <button onClick={this.postHandler}>Send Post</button>
      </div>
    );
  }
}

export default SendPost;
