import React, { Component } from 'react';
import HOCComponent from "./"
class NewChild extends Component {
    constructor() {
        super();
        this.state = {
          number: 0,
        };
      }
    
      clickHandler = () => {
        this.setState(prevState=>({
          number:prevState.number + 1,
        }));
      };

    render() {
        return (
            <div>
                <button onClick={this.clickHandler}>Cycles + 1</button>
                <h3>Number of Cycles : {this.state.number}</h3>
            </div>
        );
    }
}

export default NewChild;
