import React, { useState } from "react";

const Counter = () => {
  const [number, setNumber] = useState(0);
  const [name, setName] = useState("Marzieh");
  
  const nameHandler=()=>{
    setName("Ali")
  }

  const clichHandler = () => {
    setNumber(prevNumber => prevNumber+1);
  };

  return (
    <div>
      <h1>{name}</h1>
      <button onClick={nameHandler}>Change name</button>

      <h1>{number}</h1>
      <button onClick={clichHandler}>Up One</button>
    </div>
  );
};

export default Counter;
