import {toast } from "react-toastify";

export const notify = (text, type) =>{
    if(type==="success"){
        toast.success(text);
    }else{
        toast.error(text);
    }
};





toast.success('ðŸ¦„ Wow so easy!', {
    position: "top-right",
    autoClose: 5000,
    hideProgressBar: false,
    closeOnClick: true,
    pauseOnHover: true,
    draggable: true,
    progress: undefined,
    });


