import React, { Component } from 'react';

class ErrorBoundary extends Component {
    constructor(){
        super();
        this.state={
            error:false,
            errorMessage:""
        }
    }

    componentDidCatch(error,info){
        this.setState({
            error:true,
            errorMessage:error
        })
    }
    render() {
        if(this.state.err){
            return <p>{this.state.errorMessage}</p>
        }else{
            return this.props.children
        }
    }
}

export default ErrorBoundary;
