class SendPost extends Component {
    constructor(props) {
      super(props);
      this.state = {
        title: "",
        body: "",
      };
    }
  
    changeHandler = (event) => {
        console.log(event.target.name);
      this.setState({
        [event.target.name]: event.target.value,
      });
    };
  
    postHandler = () => {
      const data = {
        title: this.state.title,
        body :this.state.body,
      };
      axios
        .post("https://jsonplaceholder.typicode.com/posts", { data })
        .then((response) => console.log(response));
    };
  
    render() {
      const { title, body } = this.state;
      return (
        <div>
          <label>Title : </label>
          <input onChange={this.changeHandler} name="title" type="text" value={title} />
          <br/><br/>
          <label>Body : </label>
          <input onChange={this.changeHandler} name="body" type="text" value={body} />
          <br/><br/>
          <button onClick={this.postHandler}>Send Post</button>
        </div>
      );
    }
  }
  
  export default SendPost;
