import React, { Component } from 'react';

class ClassEvent extends Component{

  constructor() {
    super();
    this.state = {
    };
  }

  clickHandler = () => {
    this.setState({
      paragraph: 'text 3',
    });
  };

  render() {
    return (
      <div>
        <button onClick={this.clickHandler}>Class Event</button>
      </div>
    );
  }
}

export default ClassEvent;
