import React, { Component } from "react";

class ClassEvent extends Component {
  constructor() {
    super();
    this.state = {
      text: "",
    };
  }

  clickHandler = () => {
    this.setState({});
  };

  changeHandler = () => {};

  render() {
    return (
      <div>
        <input
          type="text"
          name=""
          value={this.state.text}
          onChange={this.changeHandler}
        />
        <button onClick={this.clickHandler}>Class Event</button>
      </div>
    );
  }
}

export default ClassEvent;
